setInterval(() => { setRandomReview(); }, 22000);

function setRandomReview() {
    let randomNumber = Math.floor(Math.random() * (listReviews.length-1));
    let randomReview = listReviews[randomNumber];
    let comment = document.getElementById("comment");
    let author = document.getElementById("author");
    let position = document.getElementById("position");
  
    if(comment && author && position) {
      comment.innerHTML = '“'+randomReview.comment+'“';
      author.innerHTML = randomReview.autor;
      position.innerHTML = randomReview.prof;
    }
}

setTimeout(() => { setRandomReview(); }, 2000);


